import React from 'react';
import { Link } from 'react-router-dom';

const Header = () => {
  return (
    <header className="bg-gradient-to-r from-blue-600 to-purple-700 text-white p-6 shadow-lg">
      <div className="container mx-auto flex justify-between items-center">
        <Link to="/" className="text-3xl font-bold hover:scale-105 transition-transform duration-200 flex items-center">
          <span className="mr-2">ðŸ“±</span>
          Android App Store
        </Link>
        <nav>
          <ul className="flex space-x-6">
            <li>
              <Link to="/" className="hover:text-blue-200 transition-colors duration-200 font-medium">
                Home
              </Link>
            </li>
            <li>
              <Link to="/apps" className="hover:text-blue-200 transition-colors duration-200 font-medium">
                Apps
              </Link>
            </li>
          </ul>
        </nav>
      </div>
    </header>
  );
};

export default Header;
