import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CategoryList from '../components/CategoryList';
import AppCard from '../components/AppCard';

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [featuredApps, setFeaturedApps] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [categoriesResponse, appsResponse] = await Promise.all([
          axios.get('http://localhost:5000/api/categories'),
          axios.get('http://localhost:5000/api/apps')
        ]);

        setCategories(categoriesResponse.data);
        setFeaturedApps(appsResponse.data.slice(0, 4)); // Show first 4 apps as featured
        setLoading(false);
      } catch (error) {
        console.error('Error fetching data:', error);
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="loading text-2xl">Loading...</div>
      </div>
    );
  }

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="text-center mb-12">
        <h1 className="text-5xl font-bold mb-4 bg-gradient-to-r from-blue-600 to-purple-600 bg-clip-text text-transparent">
          Welcome to Android App Store
        </h1>
        <p className="text-xl text-gray-600 max-w-2xl mx-auto">
          Discover amazing Android apps, games, and tools. Download the best apps for your device.
        </p>
      </div>

      <section className="mb-12">
        <h2 className="text-3xl font-bold mb-6 text-gray-800 flex items-center">
          <span className="mr-3">üìÇ</span>
          Categories
        </h2>
        <CategoryList categories={categories} />
      </section>

      <section>
        <h2 className="text-3xl font-bold mb-6 text-gray-800 flex items-center">
          <span className="mr-3">‚≠ê</span>
          Featured Apps
        </h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
          {featuredApps.map(app => (
            <AppCard key={app.id} app={app} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
