import React from 'react';
import { Link } from 'react-router-dom';

const AppCard = ({ app }) => {
  return (
    <div className="app-card">
      <div className="flex flex-col items-center text-center">
        <img
          src={app.icon}
          alt={app.name}
          className="w-20 h-20 mb-4 rounded-xl shadow-md"
          onError={(e) => {
            e.target.src = 'https://via.placeholder.com/80x80?text=App';
          }}
        />
        <h3 className="font-bold text-lg mb-2 text-gray-800 line-clamp-2">{app.name}</h3>
        <p className="text-sm text-blue-600 font-medium mb-2">{app.category}</p>
        <p className="text-sm text-gray-600 mb-4 line-clamp-3">{app.description}</p>

        <div className="flex justify-between items-center w-full mb-4">
          <div className="flex items-center">
            <span className="text-yellow-500 mr-1">‚≠ê</span>
            <span className="font-semibold">{app.rating}</span>
          </div>
          <span className="text-sm text-gray-500">{app.downloads} downloads</span>
        </div>

        <div className="flex items-center justify-between w-full mb-4">
          <span className="text-sm text-gray-600">{app.size}</span>
          <span className={`text-sm font-medium ${app.price === 'Free' ? 'text-green-600' : 'text-gray-800'}`}>
            {app.price}
          </span>
        </div>

        <Link
          to={`/apps/${app.id}`}
          className="btn-primary w-full text-center"
        >
          View Details
        </Link>
      </div>
    </div>
  );
};

export default AppCard;
