import React from 'react';
import { Link } from 'react-router-dom';

const AppCard = ({ app }) => {
  return (
    <div className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg transition-shadow">
      <img src={app.icon} alt={app.name} className="w-16 h-16 mb-2" />
      <h3 className="font-semibold text-lg mb-1">{app.name}</h3>
      <p className="text-gray-600 text-sm mb-2">{app.category}</p>
      <p className="text-sm mb-2">{app.description.substring(0, 50)}...</p>
      <div className="flex justify-between items-center mb-2">
        <span className="text-sm text-gray-500">{app.downloads} downloads</span>
        <span className="text-sm font-semibold">{app.rating}â˜…</span>
      </div>
      <Link
        to={`/apps/${app.id}`}
        className="bg-blue-600 text-white px-4 py-2 rounded text-sm hover:bg-blue-700 block text-center"
      >
        View Details
      </Link>
    </div>
  );
};

export default AppCard;
