import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const AppDetail = () => {
  const { id } = useParams();
  const [app, setApp] = useState(null);

  useEffect(() => {
    const fetchApp = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/apps/${id}`);
        setApp(response.data);
      } catch (error) {
        console.error('Error fetching app details:', error);
      }
    };

    fetchApp();
  }, [id]);

  if (!app) {
    return <div className="container mx-auto p-4">Loading...</div>;
  }

  return (
    <div className="container mx-auto p-4">
      <div className="max-w-2xl mx-auto">
        <img src={app.icon} alt={app.name} className="w-24 h-24 mb-4" />
        <h1 className="text-3xl font-bold mb-2">{app.name}</h1>
        <p className="text-gray-600 mb-4">{app.category}</p>
        <p className="mb-4">{app.description}</p>
        <div className="grid grid-cols-2 gap-4 mb-4">
          <div>
            <strong>Version:</strong> {app.version}
          </div>
          <div>
            <strong>Size:</strong> {app.size}
          </div>
          <div>
            <strong>Downloads:</strong> {app.downloads}
          </div>
          <div>
            <strong>Rating:</strong> {app.rating}/5
          </div>
        </div>
        <div className="mb-4">
          <strong>Developer:</strong> {app.developer}
        </div>
        <div className="mb-4">
          <strong>Price:</strong> {app.price}
        </div>
        <button className="bg-blue-600 text-white px-6 py-2 rounded hover:bg-blue-700">
          Download
        </button>
      </div>
    </div>
  );
};

export default AppDetail;
