import React, { useEffect, useState } from 'react';
import axios from 'axios';
import CategoryList from '../components/CategoryList';
import AppCard from '../components/AppCard';

const Home = () => {
  const [categories, setCategories] = useState([]);
  const [featuredApps, setFeaturedApps] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const categoriesResponse = await axios.get('http://localhost:5000/api/categories');
        setCategories(categoriesResponse.data);

        const appsResponse = await axios.get('http://localhost:5000/api/apps');
        setFeaturedApps(appsResponse.data.slice(0, 4)); // Show first 4 apps as featured
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchData();
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-3xl font-bold mb-8">Welcome to Android App Store</h1>

      <section className="mb-8">
        <h2 className="text-2xl font-semibold mb-4">Categories</h2>
        <CategoryList categories={categories} />
      </section>

      <section>
        <h2 className="text-2xl font-semibold mb-4">Featured Apps</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          {featuredApps.map(app => (
            <AppCard key={app.id} app={app} />
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
